import React, { useState, useEffect} from 'react';
import { withStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import ListItemText from '@material-ui/core/ListItemText';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';
import './BaseSelectMultiple.css';

function BaseSelectOne(props: any) {
    const { optionsList, label, onChange, value } = props;

    const StyledFormControl = withStyles({
        root: {
            color: '#124B35',
            width: '300px',
            margin: '10px auto',
        },
        focused: {
            color: '#124B35',
        }
    })(FormControl);

    const StyledInputLabel = withStyles({
        root: {
            color: '#124B35',
            fontSize: '19px',
            width: 'max-content',
        },
        focused: {
            color: '#124B35',
        }
    })(InputLabel);

    const values: string[] = [];
    const handleChangeMultiple = (event: React.ChangeEvent<{ value: unknown }>) => {
        const { options } = event.target as HTMLSelectElement;
        for (let i = 0, l = options.length; i < l; i += 1) {
          if (options[i].selected) {
            value.push(options[i].value);
          }
        }
        onChange(value);
    };

    return (
        <StyledFormControl>
            <StyledInputLabel shrink id="base-select-one-label">
                {label}
            </StyledInputLabel>
            <Select
                labelId="demo-simple-select-placeholder-label-label"
                id="demo-simple-select-placeholder-label"
                multiple
                value={value}
                onChange={onChange}
            >
                <MenuItem value="">
                    <em>Select One</em>
                </MenuItem>
                {optionsList.map( (option: any) => (
                    <MenuItem value={option.temp}>{option.temp}</MenuItem>
                ))}
            </Select>
            <Select
                labelId="demo-mutiple-name-label"
                id="demo-mutiple-name"
                multiple
                value={value}
                onChange={onChange}
                input={<Input />}
                // MenuProps={MenuProps}
            >
                {optionsList.map( (option: any) => (
                    <MenuItem value={option.temp}>{option.temp}</MenuItem>
                ))}
            </Select>
        </StyledFormControl>
    )   
};

export default BaseSelectOne

