import React, { useState } from 'react';
import "./Registration.css";
import BaseInputField from "../../components/BaseInputField/BaseInputField";
import BaseButton from "../../components/BaseButton/BaseButton";
import { useAuth } from '../../contexts/AuthContext'
import { RouteComponentProps } from 'react-router';
import { toast } from '../../toast';

const Registration: React.FC<RouteComponentProps> = ({history}) => {
  const [email, setEmail] = useState<string>();
  const [password, setPassword] = useState('');
  const [cpassword, setCPassword] = useState('');
  const [terms, setTerms] = useState(false);
  const { signup } = useAuth();

  let validPassword = () => {
    if (password && password === cpassword)
      return true;
    else
      return false;
  };

  async function handleRegister() {
    try {
      if (!validPassword()) {
        toast("Passwords do not match");
        return;
      }
      await signup(email, password);
      toast("Registration successful!");
      history.push("/");
    } catch (error) {
      toast(`Error registering: ${error}`);
    }
  }

  return (
    <div className="login-page-container">
      <div className="login-container">
        <div className="login-subtitle">
          Registration
        </div>
        <div className="login-input">
            <BaseInputField
              width={'350px'}
              value={email}
              label={'Email Address'}
              iduser={'username'}
              password={false}
              onChange={(e: any) => setEmail(e.target.value)}
            />
        </div>
          <div className="login-input" style={{marginBottom: '5px'}}>
            <BaseInputField
              width={'350px'}
              value={password}
              label={'Password'}
              iduser={'password'}
              password={true}
              onChange={(e: any) => setPassword(e.target.value)}
            />
          </div>
          <div className="login-input" style={{marginBottom: '5px'}}>
            <BaseInputField
              width={'350px'}
              value={cpassword}
              label={'Confirm Password'}
              iduser={'confirm-password'}
              password={true}
              onChange={(e: any) => setPassword(e.target.value)}
            />
          </div>
        <div className="login-button">
          <BaseButton className="login-button" onClick={handleRegister} width={'350px'}>Reset Password</BaseButton>
        </div>
        <a className="return-login" href="/">Return to Login</a>
      </div>
    </div>
  );
};

export default Registration;
