import React from 'react';
import './MainMenu.css';
import CP_logo_CMYK_grn from '../../assets/CP_logo_CMYK_grn.jpg';
import { logoutUser } from '../../firebaseConfig'
import { toast } from '../../toast'
import { RouteComponentProps } from 'react-router';

const MainMenu: React.FC<RouteComponentProps> = ({history, location}) => {

    async function logout(){
        await logoutUser();
        toast('You have logged out!');
        history.push({
          pathname: '/',
          state: {email: '', password: ''},
        });
      }

      function launchSurvey() {
        if (location.state) {
          history.push('/survey', location.state);
        }
      }

      function launchNotifications() {
        if (location.state) {
          history.push('/editNotifs', location.state);
        }
      }

      function launchProfile() {
        if (location.state) {
          history.push('/editProfile', location.state);
        }
      }

  return (
    <div className="menu-container">
        <img src={CP_logo_CMYK_grn} />
        <h1>Thermal Comfort</h1>
    </div>
  )
};

export default MainMenu
