import React, { useState, useEffect} from 'react';
import { withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import './BaseSelectOne.css';

function BaseSelectOne(props: any) {
    const { optionsList, label, onChange } = props;

    const StyledFormControl = withStyles({
        root: {
            color: '#124B35',
            width: '300px',
            margin: '10px auto',
        },
        focused: {
            color: '#124B35',
        }
    })(FormControl);

    const StyledInputLabel = withStyles({
        root: {
            color: '#124B35',
            fontSize: '19px',
            width: 'max-content',
        },
        focused: {
            color: '#124B35',
        }
    })(InputLabel);


    return (
        <StyledFormControl>
            <StyledInputLabel shrink id="demo-simple-select-placeholder-label-label">
                {label}
            </StyledInputLabel>
            <Select
                labelId="demo-simple-select-placeholder-label-label"
                id="demo-simple-select-placeholder-label"
                value='Hi'
                onChange={onChange}
            >
            <MenuItem value="">
                <em>Select One</em>
            </MenuItem>
            {optionsList.map( (option: any) => (
                <MenuItem value={option.temp}>{option.temp}</MenuItem>
            ))}
            </Select>
        </StyledFormControl>
    )   
};

export default BaseSelectOne

