import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar,
  IonItem, IonButton, IonInput, IonCard, IonCardHeader,
  IonCardContent, IonCardTitle, IonLabel } from '@ionic/react';
import React, { useState } from 'react';
import { loginUser } from '../../firebaseConfig';
import "./HomePage.css";
import BaseButton from "../../components/BaseButton/BaseButton";
import { RouteComponentProps } from 'react-router';
import { toast } from '../../toast';

const Home: React.FC<RouteComponentProps> = ({history}) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  async function login() {
    const res = await loginUser(email, password);

    if (res && res.user) {
      setPassword("");
      setEmail("");
      history.push({
        pathname: 'landingPage',
        state: { uid: res.user.uid},
      });

    } else {
      toast('Error logging in with your credentials');
    }
  }

  function goRegister() {
    history.push({
      pathname: '/registration'
    });
  }

  function goResetPassword() {
    history.push({
      pathname: '/resetPassword',
    });
  }

  return (
    <div className="login-page-container">
      <div className="login-container">
        <div className="login-title">
          Cal Poly Thermal Comfort
        </div>
        <IonItem>
          <IonLabel position="stacked">Email</IonLabel>
          <IonInput
            placeholder="Email"
            value={email}
            onIonChange={(e: any) => setEmail(e.target.value)}
          />
        </IonItem>
        <IonItem>
          <IonLabel position="stacked">Password</IonLabel>
          <IonInput
            type="password"
            placeholder="Password"
            value={password}
            onIonChange={(e: any) => setPassword(e.target.value)}
          />
        </IonItem>
          <IonLabel><a href="/resetPassword">Forgot password?</a></IonLabel>
          <BaseButton onClick={login} width={'350px'}>Login</BaseButton>
          <BaseButton onClick={goRegister} width={'350px'}>Register</BaseButton>
      </div>
    </div>
  );

};

export default Home;
