import React from 'react';
import './Survey.css';
import BaseButton from '../BaseButton/BaseButton'
import BaseInputField from '../BaseInputField/BaseInputField';
import BaseSelectOne from '../BaseSelectOne/BaseSelectOne';
import BaseSelectMultiple from '../BaseSelectMultiple/BaseSelectMultiple';


function Survey() {
  const tempInRoomList = [
    {id: 1, temp: 'Cold'},
    {id: 2, temp: 'Cool'},
    {id: 3, temp: 'Slightly Cool'},
    {id: 4, temp: 'Neutral: neither cool or warm'},
    {id: 5, temp: 'Slightly Warm'},
    {id: 6, temp: 'Warm'},
    {id: 7, temp: 'Hot'},
  ]
  const [tempInRoom, setTempInRoom] = React.useState('');

  const preferFeelList = [
    {id: 1, temp: 'Cooler'},
    {id: 2, temp: 'No Change'},
    {id: 3, temp: 'Warmer'},
  ]
  const [preferFeel, setPreferFeel] = React.useState('');

  const airCirculationList = [
    {id: 1, temp: 'Cooler'},
    {id: 2, temp: 'No Change'},
    {id: 3, temp: 'Warmer'},
  ]
  const [airCirculation, setAirCirculation] = React.useState('');

  const humidList = [
    {id: 1, temp: 'Too dry'},
    {id: 2, temp: 'Slightly dry'},
    {id: 3, temp: 'Just right'},
    {id: 4, temp: 'Slightly humid'},
    {id: 5, temp: 'Too humid'},
  ]
  const [humid, setHumid] = React.useState('');

  const clothingList = [
    {id: 1, temp: 'Minimal [shorts, shirt]'},
    {id: 2, temp: 'Average [pants, shirt]'},
    {id: 3, temp: 'Heavy [pants, jacket/sweater/sweatshirt]'},
  ]
  const [clothing, setClothing] = React.useState([]);

  return (
    <div className="survey-container">
      <div className="baseinput-div">
        <BaseInputField
          label={'What building are you in?'}
          iduser={'building'}
          width={'300px'}
        />
      </div>
      <div className="baseinput-div">
        <BaseInputField
          label={'What room are you in?'}
          iduser={'room'}
          width={'300px'}
        />
      </div>

      <p>How does the temperature feel in the room?</p>
      <BaseSelectOne
        label={'How does the temperature feel in the room?'}
        optionsList={tempInRoomList}
        value={tempInRoom}
        onChange={(e: any) => setTempInRoom(e.target.value)}
      />

      <p>How does the temperature feel in the room?</p>
      <BaseSelectOne
        label={'How would you prefer for it to feel?'}
        optionsList={preferFeelList}
        value={preferFeel}
        onChange={(e: any) => setPreferFeel(e.target.value)}
      />
      <BaseSelectOne
        label={'How do you feel about the air circulation in the room?'}
        optionsList={airCirculationList}
        value={airCirculation}
        onChange={(e: any) => setAirCirculation(e.target.value)}
      />
      <BaseSelectOne
        label={'How humid does the room feel?'}
        optionsList={humidList}
        value={humid}
        onChange={(e: any) => setHumid(e.target.value)}
      />
      <BaseSelectOne
        label={'What clothing layers are you wearing?'}
        optionsList={clothingList}
        value={clothing}
        onChange={(e: any) => setClothing(e.target.value)}
      />
      <BaseSelectMultiple
        label={'What activities have you done in the past 10-20 minutes?'}
        optionsList={clothingList}
        value={clothing}
        onChange={(e: any) => setClothing(e.target.value)}
      />
    </div>
  )
};

export default Survey
