import React, { useState, useEffect} from 'react';
import { withStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import ListItemText from '@material-ui/core/ListItemText';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';
import './BaseSelectMultiple.css';

function BaseSelectOne(props: any) {
    const { optionsList, label, onChange, value } = props;

    const StyledFormControl = withStyles({
        root: {
            color: '#124B35',
            width: '300px',
            margin: '10px auto',
        },
        focused: {
            color: '#124B35',
        }
    })(FormControl);

    const StyledInputLabel = withStyles({
        root: {
            color: '#124B35',
            fontSize: '19px',
            width: 'max-content',
        },
        focused: {
            color: '#124B35',
        }
    })(InputLabel);


    return (
        <StyledFormControl>
            <StyledInputLabel shrink id="base-select-one-label">
                {label}
            </StyledInputLabel>
            <Select
                labelId="demo-simple-select-placeholder-label-label"
                id="demo-simple-select-placeholder-label"
                multiple
                value={value}
                onChange={onChange}
            >
                <MenuItem value="">
                    <em>Select One</em>
                </MenuItem>
                {optionsList.map( (option: any) => (
                    <MenuItem value={option.temp}>{option.temp}</MenuItem>
                ))}
            </Select>
            <Select
                labelId="demo-mutiple-checkbox-label"
                id="demo-mutiple-checkbox"
                multiple
                value={value}
                onChange={onChange}
                input={<Input />}
                renderValue={(selected) => (selected as string[]).join(', ')}
            >
                {optionsList.map((option) => (
                    <MenuItem key={option.temp} value={name}>
                    <Checkbox checked={personName.indexOf(name) > -1} />
                    <ListItemText primary={name} />
                    </MenuItem>
                ))}
        </Select>
        </StyledFormControl>
    )   
};

export default BaseSelectOne

