import React from 'react';
import './EditNotifications.css';
import BaseButton from '../BaseButton/BaseButton'
import SecondaryButton from "../../components/SecondaryButton/SecondaryButton";
import BaseCheckbox from "../../components/BaseCheckbox/BaseCheckbox";
import FormGroup from "@material-ui/core/FormGroup";
import { pushProfile, getProfile } from '../../firebaseConfig';
import { useAuth } from '../../contexts/AuthContext'
import { toast } from '../../toast'

function EditNotifications(props: any) {
  const { toggleState } = props;
  const { currentUser } = useAuth();

  const [state, setState] = React.useState({
    checked8am: false,
    checked9am: false,
    checked10am: false,
    checked11am: false,
    checked12pm: false,
    checked1pm: false,
    checked2pm: false,
    checked3pm: false,
    checked4pm: false,
    checked5pm: false,
    checked6pm: false,
    checked7pm: false,
    checked8pm: false,
  });

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const [calPolyStatus, setCalPolyStatus] = React.useState('');
  const [biologicalSex, setBiologicalSex] = React.useState('');
  const [genderIdentification, setGenderIdentification] = React.useState('');
  const [ethnicity, setEthnicity] = React.useState('');
  const [disability, setDisability] = React.useState('');
  const [age, setAge] = React.useState('');
  const [indoorThermalPreference, setIndoorThermalPreference] = React.useState('');
  const [response, setResponse] = React.useState('');

  React.useEffect(() => { //populate with current profile data if any
    getProfile(currentUser.email).then((document) => {
      if (document.exists) {
        const profileData = document.data();
        console.log(profileData);
        if (profileData) {
          setCalPolyStatus(profileData.calPolyStatus);
          setBiologicalSex(profileData.biologicalSex);
          setGenderIdentification(profileData.genderIdentification);
          setEthnicity(profileData.ethnicity);
          setDisability(profileData.disability);
          setAge(profileData.age);
          setIndoorThermalPreference(profileData.indoorThermalPreference);
          setResponse(profileData.response);
        }
      }
    }).catch((error) => {
      console.error(error);
    });
  }, []);

  function postProfile() {
    if (currentUser) {
      calPolyStatus != '' ? profileData.calPolyStatus = calPolyStatus : canSubmit = false;
      biologicalSex != '' ? profileData.biologicalSex = biologicalSex : canSubmit = false;
      genderIdentification != '' ? profileData.genderIdentification = genderIdentification : canSubmit = false;
      ethnicity != '' ? profileData.ethnicity = ethnicity : canSubmit = false;
      disability != '' ? profileData.disability = disability : canSubmit = false;
      age != '' ? profileData.age = age : canSubmit = false;
      indoorThermalPreference != '' ? profileData.indoorThermalPreference = indoorThermalPreference : canSubmit = false;
      profileData.response = response;

      if(canSubmit){
        try {
          pushProfile(profileData, currentUser.email);
          toast(`Thank you! Profile edited successfully.`);
        }
        catch(error) {
          toast(`Error editing profile: ${error}`);
        }
        toggleState();
      }
      else {
        toast('Please answer all of the non-optional questions before submitting.');
      }
    }
  }

  return (
    <div className="editnotifs-container">
      <div className="top-buttons">
            <SecondaryButton onClick={toggleState}>Back</SecondaryButton>
        </div>
      <h1>Edit Notifications</h1>
      <h3>Please select times to be notified to take the survey</h3>
      <FormGroup>
        <BaseCheckbox checked={state.checked8am} onChange={handleChange} name="checked8am" label="8:00 AM" />
        <BaseCheckbox checked={state.checked9am} onChange={handleChange} name="checked9am" label="9:00 AM" />
        <BaseCheckbox checked={state.checked10am} onChange={handleChange} name="checked10am" label="10:00 AM" />
        <BaseCheckbox checked={state.checked11am} onChange={handleChange} name="checked11am" label="11:00 AM" />
        <BaseCheckbox checked={state.checked12pm} onChange={handleChange} name="checked12pm" label="12:00 PM" />
        <BaseCheckbox checked={state.checked1pm} onChange={handleChange} name="checked1pm" label="1:00 PM" />
        <BaseCheckbox checked={state.checked2pm} onChange={handleChange} name="checked2pm" label="2:00 PM" />
        <BaseCheckbox checked={state.checked3pm} onChange={handleChange} name="checked3pm" label="3:00 PM" />
        <BaseCheckbox checked={state.checked4pm} onChange={handleChange} name="checked4pm" label="4:00 PM" />
        <BaseCheckbox checked={state.checked5pm} onChange={handleChange} name="checked5pm" label="5:00 PM" />
        <BaseCheckbox checked={state.checked6pm} onChange={handleChange} name="checked6pm" label="6:00 PM" />
        <BaseCheckbox checked={state.checked7pm} onChange={handleChange} name="checked7pm" label="7:00 PM" />
        <BaseCheckbox checked={state.checked8pm} onChange={handleChange} name="checked8pm" label="8:00 PM" />
      </FormGroup>
      <div className='editprofile-submit'>
        <BaseButton width={'300px'} onClick={postProfile}>Submit</BaseButton>
      </div>
    </div>
  )
};

export default EditNotifications
