import React from 'react';
import './EditNotifications.css';
import BaseButton from '../BaseButton/BaseButton'
import SecondaryButton from "../../components/SecondaryButton/SecondaryButton";
import BaseCheckbox from "../../components/BaseCheckbox/BaseCheckbox";
import FormGroup from "@material-ui/core/FormGroup";
import { pushNotifs, getNotifs } from '../../firebaseConfig';
import { useAuth } from '../../contexts/AuthContext'
import { toast } from '../../toast'

function EditNotifications(props: any) {
  const { toggleState } = props;
  const { currentUser } = useAuth();

  const [state, setState] = React.useState({
    eightAm: false,
    nineAm: false,
    tenAm: false,
    elevenAm: false,
    twelvePm: false,
    onePm: false,
    twoPm: false,
    threePm: false,
    fourPm: false,
    fivePm: false,
    sixPm: false,
    sevenPm: false,
    eightPm: false,
  });

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  React.useEffect(() => { //populate with current profile data if any
    getNotifs(currentUser.email).then((document) => {
      if (document.exists) {
        const notifsData = document.data();
        if (notifsData) {
          setState({ ...state, eightAm: notifsData.eightAm });
          setState({ ...state, checked8am: notifsData.eightAm });
        }
      }
    }).catch((error) => {
      console.error(error);
    });
  }, []);

  function postProfile() {
    if (currentUser) {
      calPolyStatus != '' ? profileData.calPolyStatus = calPolyStatus : canSubmit = false;
      biologicalSex != '' ? profileData.biologicalSex = biologicalSex : canSubmit = false;
      genderIdentification != '' ? profileData.genderIdentification = genderIdentification : canSubmit = false;
      ethnicity != '' ? profileData.ethnicity = ethnicity : canSubmit = false;
      disability != '' ? profileData.disability = disability : canSubmit = false;
      age != '' ? profileData.age = age : canSubmit = false;
      indoorThermalPreference != '' ? profileData.indoorThermalPreference = indoorThermalPreference : canSubmit = false;
      profileData.response = response;

      try {
        pushNotifs(state, currentUser.email);
        toast(`Thank you! Notifications edited successfully.`);
      }
      catch(error) {
        toast(`Error editing notifications: ${error}`);
      }
      toggleState();
    }
  }

  return (
    <div className="editnotifs-container">
      <div className="top-buttons">
            <SecondaryButton onClick={toggleState}>Back</SecondaryButton>
        </div>
      <h1>Edit Notifications</h1>
      <h3>Please select times to be notified to take the survey</h3>
      <FormGroup>
        <BaseCheckbox checked={state.eightAm} onChange={handleChange} name="checked8am" label="8:00 AM" />
        <BaseCheckbox checked={state.nineAm} onChange={handleChange} name="checked9am" label="9:00 AM" />
        <BaseCheckbox checked={state.tenAm} onChange={handleChange} name="checked10am" label="10:00 AM" />
        <BaseCheckbox checked={state.elevenAm} onChange={handleChange} name="checked11am" label="11:00 AM" />
        <BaseCheckbox checked={state.twelvePm} onChange={handleChange} name="checked12pm" label="12:00 PM" />
        <BaseCheckbox checked={state.onePm} onChange={handleChange} name="checked1pm" label="1:00 PM" />
        <BaseCheckbox checked={state.twoPm} onChange={handleChange} name="checked2pm" label="2:00 PM" />
        <BaseCheckbox checked={state.threePm} onChange={handleChange} name="checked3pm" label="3:00 PM" />
        <BaseCheckbox checked={state.fourPm} onChange={handleChange} name="checked4pm" label="4:00 PM" />
        <BaseCheckbox checked={state.fivePm} onChange={handleChange} name="checked5pm" label="5:00 PM" />
        <BaseCheckbox checked={state.sixPm} onChange={handleChange} name="checked6pm" label="6:00 PM" />
        <BaseCheckbox checked={state.sevenPm} onChange={handleChange} name="checked7pm" label="7:00 PM" />
        <BaseCheckbox checked={state.eightPm} onChange={handleChange} name="checked8pm" label="8:00 PM" />
      </FormGroup>
      <div className='editprofile-submit'>
        <BaseButton width={'300px'} onClick={postProfile}>Submit</BaseButton>
      </div>
    </div>
  )
};

export default EditNotifications
